package pattern;

public class pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=20;
		for(int i=1;i<=n;i++)
		{
			if(i<10)
			
			System.out.print(" ");
			
			System.out.print(i+ " " );
			
			if(i%5==0)
			{
			System.out.println();	
			}
		}

	}

}
